export default {
  name: 'AppSidebar',
  props: {
    drawer: {
      type: Boolean,
      required: true
    },
    miniVariant: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      localDrawer: this.drawer
    };
  },
  watch: {
    drawer(val) {
      this.localDrawer = val;
    },
    localDrawer(val) {
      this.$emit('update:drawer', val);
    }
  }
};
